import React, { Component } from 'react';
import _ from 'lodash';

class TableBody extends Component {
  // renderCell = (item, column) => {
  //   if (column.content) return column.content(item); // if column.content is truthi we are going to call this as a fun and give it an argument and it would be item an give us a react elemnt
  //   _.get(item, column.path); //age halate bala nashod va nadashtim current item return mishe
  // };
  render() {
    const { data, columns } = this.props;
    return (
      <tbody>
        {data.map(item => (
          <tr>
            {columns.map(column => (
              <td>{this.renderCell(item, column)}</td>
            ))}
          </tr>
        ))}
      </tbody>
    );
  }
}

export default TableBody;
