import React, { Component } from 'react';
import Like from './common/like';

class MoviesTable extends Component {
  render() {
    const { movies, onDelete, onSort } = this.props; //onDelete hamoon this.handleDelete hast ke baraye props kardan avazesh kardim chon ke ona joze movie component hast va injori ba fn refrences beheshon dastresi peyda mikonim// vaghti az fn component be class component taghir dadim bayad jaye props benevisim this.props chon inja dge parametr nist va khodesh dare props mishe az jaye dge
    return (
      <table className="table">
        <thead>
          <tr>
            <th onClick={() => onSort('title')}>Title</th>
            <th onClick={() => onSort('genre.name')}>Genre</th>
            <th onClick={() => onSort('numberInStock')}>Stock</th>
            <th onClick={() => onSort('dailyRentalRate')}>Rate</th>
            <th />
          </tr>
        </thead>
        <tbody>
          {movies.map(movie => (
            <tr key={movie._id}>
              <td>{movie.title}</td>
              <td>{movie.genre.name}</td>
              <td>{movie.numberInStock}</td>
              <td>{movie.dailyRentalRate}</td>
              <td>
                <Like />
              </td>
              <td>
                <button onClick={() => onDelete(movie)} className="btn btn-danger btn-sm">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default MoviesTable;
